import React, {useEffect, useState} from 'react';
import './css/RightSidebar.css'
import UserThumbnail from './UserThumbnail';
import axios from 'axios'

function RightSidebar(props) {

    const [userThumbnails, setUserThumbnails] = useState([]);

    

    useEffect(() => {
        if (props.currentServerId != null) {
            axios.get(`http://localhost:5000/servers/serverUsers/${props.currentServerId}`, {headers: 
                {
                "x-access-token": localStorage.getItem("authToken")
                }
            })
            .then((res) => {
                setUserThumbnails(res.data);
            });
        } else {
            //maybe populate it with friends
        }
    }, [props.currentServerId]);


    return(

        <div className="leftsidebar-wrapper">
            <ul className="list-group">
                {userThumbnails.map((el, i) => (
                    <UserThumbnail userId={el._id} username={el.username} thumbnailDataId={el.thumbnailDataId} key={el._id}/>
                ))}
            </ul>
        </div>
    )
    
} 

export default RightSidebar;
