import React, {useState} from 'react';
import './css/Login.css'

function Login(props) {
    const [registrationMode, setRegistrationMode] = useState(false);
    const [usernameInput, setUsernameInput] = useState('');
    
    const toggleRegistrationMode = () => {
        if (!registrationMode) {
        setRegistrationMode(true);
        } else {
            setRegistrationMode(false);
        }
    }

    const handleRegistration = () => {
        const fd = new FormData();
        fd.append('username', )
    }

    if (!registrationMode){
        return (
            <div className="login text-center">
                <div class="input-group mb-3 ">
                    <input type="text" className="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1"/>
                </div>
                <div class="input-group mb-3">
                    <input type="text" className="form-control" placeholder="Password" aria-label="Password" aria-describedby="basic-addon1"/>
                </div>
                <button type="button" className="btn btn-outline-primary">Login</button>
                <div className="user-select-none" onClick={toggleRegistrationMode}>Register new account</div>
            </div>
        )
    } else {
        return (
            <div className="login text-center">
                <div class="input-group mb-3 ">
                    <input type="text" id="reg-username" className="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1"/>
                </div>
                <div class="input-group mb-3 ">
                    <span className="input-group-text" id="basic-addon1">@</span>
                    <input type="text" className="form-control" placeholder="Email" aria-label="Email" aria-describedby="basic-addon1"/>
                </div>
                <div class="input-group mb-3">
                    <input type="text" className="form-control" placeholder="Password" aria-label="Password" aria-describedby="basic-addon1"/>
                </div>
                <div class="input-group mb-3">
                    <input type="text" className="form-control" placeholder="Confirm Password" aria-label="Confirm Password" aria-describedby="basic-addon1"/>
                </div>
                <button type="button" className="btn btn-outline-primary">Register</button>
                <div className="user-select-none" onClick={toggleRegistrationMode}>Return to login</div>
            </div>
        )
    }
}

export default Login;
